dict.string:
#!/bin/bash
[[ -z "$TM_FILEPATH" ]] && TM_TMPFILE=$(mktemp -t pythonInTerm)
: "${TM_FILEPATH:=$TM_TMPFILE}"; cat >"$TM_FILEPATH"

TPY=${TM_PYTHON:-python}

esc () {
STR="$1" ruby <<"RUBY"
   str = ENV['STR']
   str = str.gsub(/'/, "'\\\\''")
   str = str.gsub(/[\\"]/, '\\\\\\0')
   print "'#{str}'"
RUBY
}

run rxvt -e bash -c "clear; cd ${TM_DIRECTORY}; ${TPY} ${TM_FILEPATH}; rm -f ${TM_TMPFILE}; echo Press enter to continue...; read clear"
