dict.string:
get_pw () { res=$(CocoaDialog secure-inputbox --float --no-newline \ --title 'Encrypt Text With AES 128' \ --informative-text "$1" \ --button1 "$2" --button2 Cancel) [[ ${res:0:1} == 1 ]] && echo -n "${res:2}" }
pw1=$(get_pw 'What password should be used?' Continue) [[ -z "$pw1" ]] && exit_discard pw2=$(get_pw 'Enter password again to verify.' Encrypt)
if [[ "$pw1" == "$pw2" ]]; then openssl enc -e -aes128 -base64 -pass "pass:$pw1" else exit_show_tool_tip 'Password mismatch! Please play again.' fi
