dict.string:
#!/bin/sh
{ APP_PATH=$(ps -xwwp $PPID -o command|grep -o '.*.app') osascript -e 'tell app "TextMate" to quit'
for (( i = 0; i < 500 && $(echo $(ps -xp $PPID|wc -l))-1; i++ )); do sleep .2; done
if [[ $(ps -xp $PPID|wc -l) -ne 2 ]]; then open "$APP_PATH" else echo >/dev/console "$(date +%Y-%m-%d\ %H:%M:%S): TextMate is still running. Relaunch aborted." fi
} &>/dev/null &
