#!/usr/bin/env ruby -w require ENV['TM_BUNDLE_SUPPORT'] + "/lib/tokenizer.rb" require ENV['TM_BUNDLE_SUPPORT'] + "/lib/node.rb"
def strip_tags(html) return html if html.empty? || !html.include?('<') output = "" tokenizer = HTML::Tokenizer.new(html) while token = tokenizer.next node = HTML::Node.parse(nil, 0, 0, token, false) output += token unless (node.kind_of? HTML::Tag) or (token =~ /^<!/) end return output end
print strip_tags(STDIN.read)
